(function(){Vue.component("convos-message-enable-notifications",{props:["dialog","msg","user"],data:function(){return{clicked:false};},methods:{enable:function(enable){var activated=enable?"enabled":"disabled";this.enableNotifications(enable);this.clicked=true;this.user.getActiveDialog().addMessage({type:"notice",message:'You have now '+activated+' notifications. This can be changed later in "Edit profile".'});}},template:"<div class=\"convos-message error\">\n    <a class=\"title\" href=\"#chat\">convosbot</a>\n    <div class=\"message\">\n      Do you want desktop notifications for important messages?\n      <p>\n        <button :disabled=\"clicked\" @click=\"enable(true)\" class=\"btn waves-effect waves-light\">Yes</button>\n        <button :disabled=\"clicked\" @click=\"enable(false)\" class=\"btn waves-effect waves-light grey\">No</button>\n      </p>\n    </div>\n  </div>\n"})})();